import React from "react";
import { Link } from "react-router-dom";
import "./FoodMenu.css";
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from "reactstrap";

function FoodMenu(props) {
  return (
    <section className="col-md-4">
      <Card>
        <CardBody>
          {props.snacks.length===0 && <CardTitle className="font-weight-bold text-center">

            Food Menu
          </CardTitle>
          }
          {props.drinks.length===0 && <CardTitle className="font-weight-bold text-center">

            Drinks Menu
          </CardTitle>
          }

          <CardText>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </CardText>
          <ListGroup>
            {snacks.length !==0 && snacks.map(snack => (
              <Link to={`/snacks/${snack.id}`} key={snack.id}>
                <ListGroupItem>{snack.name}</ListGroupItem>
              </Link>
            ))
            }

            {drinks.length !==0 && drinks.map(drink => (
              <Link to={`/snacks/${drink.id}`} key={drink.id}>
                <ListGroupItem>{drink.name}</ListGroupItem>
              </Link>
            ))
            }
          </ListGroup>
        </CardBody>
      </Card>
    </section>
  );
}

export default FoodMenu;
