import React from "react";
import { Link } from "react-router-dom";
import "./FoodMenu.css";
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from "reactstrap";

function FoodMenu({ items}) {
  return (
    <section className="col-md-4">
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">


          </CardTitle>

          {/* {drinks.length!==0 && <CardTitle className="font-weight-bold text-center">

            Drinks Menu
          </CardTitle>
          } */}

          <CardText>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </CardText>
          <ListGroup>
            {items!=='snacks'? items.map(item => (
              <Link to={`/snacks/${item.id}`} key={item.id}>
                <ListGroupItem>{item.name}</ListGroupItem>
              </Link>
            )) :

            items.map(d => (
              <Link to={`/drinks/${d.id}`} key={d.id}>
                <ListGroupItem>{d.name}</ListGroupItem>
              </Link>
            ))
            }

            {/* {drinks.length ===0 && drinks.map(drink => (
              <Link to={`/drinks/${drink.id}`} key={drink.id}>
                <ListGroupItem>{drink.name}</ListGroupItem>
              </Link>
            ))
            } */}
          </ListGroup>
        </CardBody>
      </Card>
    </section>
  );
}

export default FoodMenu;
